mapper.update:
update user_toy set
  choice_type = #{choiceType, jdbcType=INTEGER}
where id = #{id} and user_no = #{userNo, jdbcType=VARCHAR}

mapper.select:
select
  id,
from user_toy
where
    user_no = #{userNo, jdbcType=VARCHAR}
    and toy_no = #{toyNo, jdbcType=VARCHAR}
    and choice_type = 0 limit #{limit, jdbcType=INTEGER}
